import React, { useState, useEffect, useCallback } from 'react';

const FormControl = (props) => {

  // fetch api states

  const [error, setError] = useState(null);
  const [json, setjson] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // **


  const [enteredName, setEnteredName] = useState('');
  const [enteredBody, setEnteredBody] = useState('');
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);
  const [enteredBodyTouched, setEnteredBodyTouched] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== '';
  const enteredBodyIsValid = enteredBody.trim() !== '';
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;
  const BodyInputIsInvalid = !enteredBodyIsValid && enteredBodyTouched;

// fetch fetch ****************************************************************











  function addMovieHandler(movie) {
    console.log(movie);
  }

  let content = <p>Found no movies.</p>;

  if (json.length > 0) {
    // content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }





// ************************************************************************************************


  let formIsValid = false;

  if (enteredNameIsValid && enteredBodyIsValid) { 
    formIsValid = true;
  }

  const enteredObject = {
    title : enteredName,
    body : enteredBody
  }

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);

  };
  const BodyInputChangeHandler = (event) => {
    setEnteredBody(event.target.value);
  };

  const nameInputBlurHandler = (event) => {
    setEnteredNameTouched(true);
  };

  const BodyInputBlurHandler = (event) => {
    setEnteredBodyTouched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    setEnteredNameTouched(true);
    setEnteredBodyTouched(true);

    if (!(enteredNameIsValid && enteredBodyIsValid)) {
      return;
    }
    fetch('https://jsonplaceholder.typicode.com/post', {
      method: 'POST',
      body: JSON.stringify({
        title: 'foo',
        body: 'bar',
        userId: 1,
      }),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      },
    })
      .then((response) => response.json()).then(res => {

        if (!res.ok && res) {
           throw new Error(res.error);
        }
        return res;
      })
      .catch(err => console.log(err+' : some error occured'));
    console.log(enteredName);

    // nameInputRef.current.value = ''; => NOT IDEAL, DON'T MANIPULATE THE DOM
    setEnteredName('');
    setEnteredBody('');
    setEnteredNameTouched(false);
    setEnteredBodyTouched(false);


// fetch api json placeholder










    console.log(enteredObject)
  };

  const nameInputClasses = nameInputIsInvalid
    ? 'form-control invalid'
    : 'form-control';


    fetch('https://jsonplaceholder.typicode.com/todos/1')
    .then(response => response.json())
    .then(json => console.log(json))





  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Title</label>
        <input
          type='text'
          id='name'
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && (
          <p className='error-text'>Title must not be empty.</p>
        )}
      </div>

      <div className={nameInputClasses}>
        <label htmlFor='name'>Body</label>
        <input
          type='text'
          id='name'
          onChange={BodyInputChangeHandler}
          onBlur={BodyInputBlurHandler}
          value={enteredBody}
        />
        {BodyInputIsInvalid && (
          <p className='error-text'>Body must not be empty.</p>
        )}
      </div>


      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default FormControl;
